function simplify(obj) {
    if (Object.prototype.toString.call(obj) == "[object Array]") {
        var out = []; 
        if (obj.length > 1) {
            for (var i in obj) {
                out[i] = simplify(obj[i]);
            }
            return out;
        } else if (obj.length == 1) {
            out = simplify(obj[0]);
            return out;
        } else {
            return "";
        }
   } else if (Object.prototype.toString.call(obj) == "[object Object]") {
       var out = {};
       for (var property in obj) {
           if( property === 'RESPONSE')
               outProperty = property.toLowerCase();
           else {
               outProperty = property[0].toLowerCase();
               outProperty = outProperty + property.substr(1, property.length);
           }
           out[outProperty] = simplify(obj[property]);
       }
        return out;
    } else {
        return obj;
    }
}

function normaliseJsonObject(jsonObject, out, akey){
    for(var key in jsonObject){
        if (typeof jsonObject[key] === 'object') {
            //console.log("o  "+akey + " " + key);
            if( key !== "$" ) {
                out[key] = {};
                normaliseJsonObject(jsonObject[key], out, key);
            } else {
                normaliseJsonObject(jsonObject[key], out, akey);
            }

        } else{
            //console.log("v  "+akey + " " + key + ": " + jsonObject[key]);
            var skey = key;
            if( key === "_")  skey = "result";
            if( key !== "$" ) out[akey][skey] = jsonObject[key];
        }
    }
};

function transform_oree(aobj) {
    var obj = aobj['response'];
    var out = {};    
    for (var property in obj) {
        if (property === '$') {
            for (var key in obj[property]) {
                out[key] = obj[property][key];
            }
        } else {
            out[property] = {};
            for (var key in obj[property]) {
                var okey = key;
                if (key === '$') {
                    for (var akey in obj[property][key]) {
                        out[property][akey] = obj[property][key][akey];
                    }
                } else {
                    if(key == '_') okey = 'result';                
                    out[property][okey] = obj[property][key]; 
                }
            }

        }
    }
    var ret = {
        response : out
    }
    return ret;
};

